import React, { Component } from 'react';
import axios from 'axios'
import './App.css';

const API_BASE_URL = "https://rata.digitraffic.fi/api/v1/";

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      response: undefined,
      shortCode: ""
    }
    //this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  async handleChange(evt) {

    let response = await axios.get(`${API_BASE_URL}metadata/stations`);
    let data = response.data;

    for (let i = 0; i < data.length; i++) {
      // if(data[i].stationName.toLowerCase() === )
    }





    this.setState({
      [evt.target.name]: evt.target.value
    })
  }

  render() {
    return (
      <div>
        <form>
          <label>
            Junan lähtöasema
                <input name="depStation" onChange={this.handleChange}></input>
          </label>
          <label>
            Junan kohdeasema
                    <input name="arrStation" onChange={this.handleChange}></input>
          </label>
          <label>
            Junan numero
                    <input name="trainNumber" onChange={this.handleChange}></input>
          </label>
          <button></button>
        </form>
      </div>
    )
  }
}

export default App;
