import React, { Component } from 'react';
import axios from 'axios'
import Results from './Results.js'
import Error from './Error.js'
import './App.css';

const API_BASE_URL = "https://rata.digitraffic.fi/api/v1/";

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      departureCode: undefined,
      arrivalCode: undefined,
      departures:
      {
        track: undefined,
        time: undefined
      },
      arrivals:
      {
        track: undefined,
        time: undefined
      },
      depFound: undefined,
      arrFound: undefined,
      submitted: undefined,
      error: false,
      trainFound: undefined
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.toCode = this.toCode.bind(this);
  }

  async toCode(evt) {
    try {
      let response = await axios.get(`${API_BASE_URL}metadata/stations`);
      let data = response.data;
      for (let i = 0; i < data.length; i++) {
        if (data[i].stationName.toLowerCase() === evt.toLowerCase() || evt.toLowerCase() + " asema" === data[i].stationName.toLowerCase()) {
          return data[i].stationShortCode;
        }
      }
    } catch{
      return undefined
    }
  }

  handleStation(evt) {

  }

  async handleChange(evt) {
    this.setState({
      [evt.target.name]: evt.target.value
    })
  }

  async handleSubmit(evt) {
    evt.preventDefault();
    let depCode = await this.toCode(this.state.depStation);
    let arrCode = await this.toCode(this.state.arrStation);

    this.setState({
      departureCode: depCode,
      arrivalCode: arrCode,
      depFound: undefined,
      arrFound: undefined
    })

    try {
      let response = await axios.get(`${API_BASE_URL}trains/latest/${this.state.trainNumber}`);
      let data = response.data;

      if (depCode !== undefined && arrCode !== undefined && data[0].hasOwnProperty('trainNumber')) {

        this.setState({
          trainFound: true
        })
        for (let i = 0; i < data[0].timeTableRows.length; i++) {
          if (data[0].timeTableRows[i].stationShortCode === this.state.departureCode && data[0].timeTableRows[i].type === "DEPARTURE") {
            let time = new Date(data[0].timeTableRows[i].scheduledTime);
            this.setState({
              departures: {
                track: data[0].timeTableRows[i].commercialTrack,
                time: (time.getHours() < 10 ? '0' + time.getHours() : time.getHours()) +
                  ':' +
                  (time.getMinutes() < 10 ? '0' + time.getMinutes() : time.getMinutes())
              },
              depFound: true
            })
            break;
          } else {
            this.setState({
              depFound: false
            })
          }
        }
        for (let i = 0; i < data[0].timeTableRows.length; i++) {
          if (data[0].timeTableRows[i].stationShortCode === this.state.arrivalCode && data[0].timeTableRows[i].type === "ARRIVAL") {
            let time = new Date(data[0].timeTableRows[i].scheduledTime);
            this.setState({
              arrivals: {
                track: data[0].timeTableRows[i].commercialTrack,
                time: (time.getHours() < 10 ? '0' + time.getHours() : time.getHours()) +
                  ':' +
                  (time.getMinutes() < 10 ? '0' + time.getMinutes() : time.getMinutes())
              },
              arrFound: true
            })
            break;
          } else {
            this.setState({
              arrFound: false
            })
          }
        }
        this.setState({
          submitted: true,
          error: false
        })
      } else {
        this.setState({
          error: true,
          submitted: false,
          trainFound: false
        })
      }
    }
    catch {
      this.setState({
        error: true,
        submitted: false,
        trainFound: false
      })
    }
  }

  render() {
    return (
      <div className="form">
        <form onSubmit={this.handleSubmit}>
          <label>
            Junan lähtöasema
          </label>
          <input type="text" name="depStation" onChange={this.handleChange} required></input>
          <label>
            Junan kohdeasema
          </label>
          <input type="text" name="arrStation" onChange={this.handleChange} required></input>
          <label>
            Junan numero
          </label>
          <input type="number" name="trainNumber" onChange={this.handleChange} required></input>
          <button className="btn btn-info">Hae tiedot</button>
        </form>
        {this.state.submitted === true && this.state.depFound === true && this.state.arrFound === true ? <Results
          depStation={this.state.depStation}
          arrStation={this.state.arrStation}
          dtrack={this.state.departures.track}
          dtime={this.state.departures.time}
          atrack={this.state.arrivals.track}
          atime={this.state.arrivals.time} /> : undefined}
        {this.state.error === true && this.state.trainFound === false ? <Error train={this.state.trainNumber} /> : undefined}
        {this.state.depFound === false || this.state.arrFound === false ? <p id="boi">Juna numero {this.state.trainNumber} ei kulje annettua reittiä.</p> : undefined}
      </div>
    )
  }
}

export default App;
