import React, { Component } from 'react';

import Results from './Results.js/index.js'
import axios from 'axios'
import './App.css';

const API_BASE_URL = "https://rata.digitraffic.fi/api/v1/";

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      response: undefined,
      station: "",
      shortCode: ""
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }


  async handleSubmit(evt) {
    evt.preventDefault();
    let response = await axios.get(`${API_BASE_URL}metadata/stations`);

    let data = response.data;
    let asema = "";
    this.setState({
      response: response
    })
    for (let i = 0; i < data.length; i++) {
      if (data[i].stationName.toLowerCase() === this.state.station || data[i].stationName.toLowerCase() === this.state.station + ' asema' || data[i].stationName.toLowerCase() === this.state.station + ' satama') {
        asema = data[i].stationShortCode;
        break;
      }
      else {
        asema = 'nani?!?!';
      }

    }
    this.setState({
      shortCode: asema
    })
  }

  handleChange(evt) {
    this.setState({
      station: evt.target.value
    })
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <div>
            Junan lähtöasema
                <input onChange={this.handleChange}></input>
          </div>
          <div>
            Junan kohdeasema
                    <input></input>
          </div>
          <div>
            Junan numero
                    <input></input>
          </div>
          <button></button>
        </form>
      </div>
    )
  }




}

export default App;
