import React, { Component } from 'react';
import axios from 'axios'
import './App.css';

const API_BASE_URL = "https://rata.digitraffic.fi/api/v1/";

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      response: undefined,
      departureCode: undefined,
      arrivalCode: undefined
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.toCode = this.toCode.bind(this);
  }

  async toCode(evt) {
    let response = await axios.get(`${API_BASE_URL}metadata/stations`);
    let data = response.data;

    for (let i = 0; i < data.length; i++) {
      if (evt.toLowerCase() === data[i].stationName.toLowerCase() || evt.toLowerCase() + "asema" === data[i].stationName.toLowerCase()) {
        return (data[i].stationShortCode);

      }
    }
  }

  //jäätiin tosiaan siihen, että toi ylläoleva paska palauttaa tyhjän objektin :D

  async handleChange(evt) {
    this.setState({
      [evt.target.name]: evt.target.value
    })
  }

  async handleSubmit(evt) {
    evt.preventDefault();

    let depCode = this.toCode(this.state.depStation);
    let arrCode = this.toCode(this.state.arrStation);

    this.setState({
      departureCode: depCode,
      arrivalCode: arrCode
    })
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Junan lähtöasema
                <input name="depStation" onChange={this.handleChange}></input>
          </label>
          <label>
            Junan kohdeasema
                    <input name="arrStation" onChange={this.handleChange}></input>
          </label>
          <label>
            Junan numero
                    <input name="trainNumber" onChange={this.handleChange}></input>
          </label>
          <button></button>
        </form>
      </div>
    )
  }
}

export default App;
