import React, { Component } from 'react';
import axios from 'axios'
import Results from './Results.js'
import './App.css';

const API_BASE_URL = "https://rata.digitraffic.fi/api/v1/";

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      response: undefined,
      departureCode: undefined,
      arrivalCode: undefined,
      departures:
      {
        track: undefined,
        time: undefined
      },
      arrivals:
      {
        track: undefined,
        time: undefined
      },
      submitted: false
    }
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.toCode = this.toCode.bind(this);
  }

  async toCode(evt) {
    let response = await axios.get(`${API_BASE_URL}metadata/stations`);
    let data = response.data;
    for (let i = 0; i < data.length; i++) {
      if (data[i].stationName.toLowerCase() === evt.toLowerCase() || evt.toLowerCase() + " asema" === data[i].stationName.toLowerCase()) {
        return data[i].stationShortCode;
      }
    }
  }

  async handleChange(evt) {
    this.setState({
      [evt.target.name]: evt.target.value
    })
  }

  async handleSubmit(evt) {
    evt.preventDefault();
    let depCode = await this.toCode(this.state.depStation);
    let arrCode = await this.toCode(this.state.arrStation);


    this.setState({
      departureCode: depCode,
      arrivalCode: arrCode,
      submitted: true
    })

    let response = await axios.get(`${API_BASE_URL}trains/latest/${this.state.trainNumber}`);
    let data = response.data;

    for (let i = 0; i < data.length; i++) {
      if (this.state.departureCode === data[i].stationShortCode && data[i].type === 'DEPARTURE') {
        this.setState({
          departures: {
            track: 'uu'//data[i].commercialTrack
          }
        })
      }
    }
  }

  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <label>
            Junan lähtöasema
                <input name="depStation" onChange={this.handleChange}></input>
          </label>
          <label>
            Junan kohdeasema
                    <input name="arrStation" onChange={this.handleChange}></input>
          </label>
          <label>
            Junan numero
                    <input name="trainNumber" onChange={this.handleChange}></input>
          </label>
          <button></button>
        </form>
        {this.state.submitted === true ? <Results depStation={this.state.depStation} arrStation={this.state.arrStation} track={this.state.departures.track} /> : <p></p>}

      </div>
    )
  }
}

export default App;
