import React, { Component } from 'react'
import './Form.css'
import axios from 'axios'


const API_BASE_URL = "https://rata.digitraffic.fi/api/v1/";




class Form extends Component {



    constructor(props) {
        super(props)
        this.state = {
            response: undefined,
            station: "",
            shortCode: ""
        }
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }


    async handleSubmit() {
        let response = await axios.get(`${API_BASE_URL}metadata/stations`);

        let data = response.data;
        this.setState({
            response: response
        })
        /* data.map(s => {
             if (s.stationName === this.state.station) {
                 this.setState({
                     shortCode: s.stationShortCode
                 })
             }
         })
         let pysäkki = this.state.station;
 */
    }

    handleChange(evt) {
        this.setState({
            station: evt.target.value
        })
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <div>
                        Junan lähtöasema
                    <input onChange={this.handleChange}></input>
                    </div>
                    <div>
                        Junan kohdeasema
                        <input></input>
                    </div>
                    <div>
                        Junan numero
                        <input></input>
                    </div>
                    <button></button>
                </form>

            </div>
        )
    }
}

export default Form
